export async function seedUsers() {
  const usersData = [
    {
      email: 'wichitchai63@gmail.com',
      password: '123456',
      display_name: 'User1',
      phone: '0800000000',
      role_id: 1
    },
    {
      email: 'b6512194@g.sut.ac.th',
      password: '123456789',
      display_name: 'User2',
      phone: '0900000000',
      role_id: 2
    }
  ];

  for (const u of usersData) {
    // 1. list users ‡∏à‡∏≤‡∏Å auth
    const { data: userList, error: listError } = await supabase.auth.admin.listUsers({
      page: 1,
      perPage: 1000
    });

    if (listError) {
      //console.error(`Error listing users:`, listError);
      continue;
    }

    let authUserId: string | null = null;
    const existingAuth = userList.users.find(user => user.email === u.email);

    if (existingAuth) {
      // ‡πÉ‡∏ä‡πâ uid ‡πÄ‡∏î‡∏¥‡∏°‡∏Ç‡∏≠‡∏á auth.users
      authUserId = existingAuth.id;
    } else {
      // ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ user ‡πÉ‡∏ô auth ‚Üí ‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÉ‡∏´‡∏°‡πà
      const { data: signUpData, error: signUpError } = await supabase.auth.admin.createUser({
        email: u.email,
        password: u.password,
        user_metadata: {
          display_name: u.display_name,
          phone: u.phone
        }
      });

      if (signUpError || !signUpData.user) {
        //console.error(`Error creating auth user for ${u.email}:`, signUpError);
        continue;
      }

      authUserId = signUpData.user.id;
    }

    // 2. insert ‡∏´‡∏£‡∏∑‡∏≠ update ‡∏•‡∏á public.Users
    const { error: upsertError } = await supabase
      .from('Users')
      .upsert(
        [{ user_id: authUserId, role_id: u.role_id }],
        { onConflict: 'user_id' } // ‡∏õ‡πâ‡∏≠‡∏á‡∏Å‡∏±‡∏ô‡∏ã‡πâ‡∏≥
      );

    if (upsertError) {
      //console.error(`Error upserting into public.Users for ${u.email}:`, upsertError);
      continue;
    }

    //console.log(`Synced user: ${u.email}`);
  }
}

